import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const EcoChart = ({ ecoData }) => {
    return (
        <ResponsiveContainer width="100%" height={400}>
            <LineChart
                data={ecoData}
                margin={{
                    top: 20, right: 30, left: 20, bottom: 5,
                }}
            >
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="carbon_footprint" stroke="#8884d8" activeDot={{ r: 8 }} />
                <Line type="monotone" dataKey="energy_consumption" stroke="#82ca9d" />
                <Line type="monotone" dataKey="water_usage" stroke="#ffc658" />
            </LineChart>
        </ResponsiveContainer>
    );
};

export default EcoChart;
