import React from 'react';
import { Line } from 'react-chartjs-2';

const EcoChart = ({ ecoData }) => {
    // Check if ecoData is empty
    if (ecoData.length === 0) {
        return <div>No data available to display the chart.</div>;
    }

    const chartData = {
        labels: ecoData.map(item => item.date), // Extract dates for X-axis
        datasets: [
            {
                label: 'Carbon Footprint',
                data: ecoData.map(item => item.carbon_footprint),
                borderColor: 'rgba(75, 192, 192, 1)',
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
            },
            {
                label: 'Energy Consumption',
                data: ecoData.map(item => item.energy_consumption),
                borderColor: 'rgba(255, 99, 132, 1)',
                backgroundColor: 'rgba(255, 99, 132, 0.2)',
            },
            {
                label: 'Water Usage',
                data: ecoData.map(item => item.water_usage),
                borderColor: 'rgba(255, 206, 86, 1)',
                backgroundColor: 'rgba(255, 206, 86, 0.2)',
            },
        ],
    };

    return (
        <div>
            <h3>Eco Data Chart</h3>
            <Line data={chartData} />
        </div>
    );
};

export default EcoChart;
