import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import EcoChart from './EcoChart'; // Import the chart component we'll use later

const Dashboard = () => {
    const [ecoData, setEcoData] = useState([]);
    const [loading, setLoading] = useState(true); // State to manage loading
    const [error, setError] = useState(null); // State to manage error messages
    const navigate = useNavigate();

    // Function to log errors or activity
    const recordActivity = (activity) => {
        console.log('Recording activity:', activity);
        // Optionally, you could send the log to a server or external logging service.
        // axios.post('http://your-logging-service.com/api/logs', activity);
    };

    useEffect(() => {
        const fetchEcoData = async () => {
            // Retrieve token from localStorage
            const token = localStorage.getItem('accessToken'); 

            if (!token) {
                navigate('/auth'); // If there's no token, redirect to login
                return;
            }

            try {
                const response = await axios.get('http://localhost:8000/api/eco-tracking/', {
                    headers: {
                        Authorization: `Bearer ${token}`, // Ensure the token is sent
                    },
                });
                setEcoData(response.data);
            } catch (error) {
                console.error('Error fetching eco data:', error);
                
                // Record the error details for future analysis
                recordActivity({
                    error: error.response ? error.response.data : 'Unknown error',
                    status: error.response ? error.response.status : 'No status',
                    message: error.message,
                    timestamp: new Date().toISOString(),
                });
                
                if (error.response && error.response.status === 403) {
                    navigate('/auth');  // If forbidden, redirect to login
                } else {
                    setError('Failed to fetch eco data. Please try again.'); // Set error message
                }
            } finally {
                setLoading(false); // Stop loading state regardless of the outcome
            }
        };

        fetchEcoData();
    }, [navigate]);

    if (loading) {
        return <div>Loading...</div>; // Display loading indicator
    }

    return (
        <div className="dashboard-container">
            <h2>Eco Tracking Dashboard</h2>
            {error && <div className="error-message">{error}</div>} {/* Display error message if exists */}
            <EcoChart ecoData={ecoData} />
        </div>
    );
};

export default Dashboard;
