import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import EcoChart from './EcoChart';
import { getAccessToken, isAuthenticated, logout } from './auth';  // Import auth functions

const Dashboard = () => {
    const [ecoData, setEcoData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchEcoData = async () => {
            if (!isAuthenticated()) {
                navigate('/auth'); // Redirect if not authenticated
                return;
            }

            const token = getAccessToken();
            try {
                const response = await axios.get('http://localhost:8000/api/eco-tracking/', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setEcoData(response.data);
            } catch (error) {
                console.error('Error fetching eco data:', error);
                if (error.response && error.response.status === 403) {
                    logout(); // Log out on forbidden access
                    navigate('/auth');
                } else {
                    setError('Failed to fetch eco data. Please try again.');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchEcoData();
    }, [navigate]);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="dashboard-container">
            <h2>Eco Tracking Dashboard</h2>
            {error && <div className="error-message">{error}</div>}
            <EcoChart ecoData={ecoData} />
        </div>
    );
};

export default Dashboard;
