import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import EcoChart from './EcoChart';
import EcoTrackingForm from './EcoTrackingForm';

const Dashboard = () => {
    const [ecoData, setEcoData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const recordActivity = (activity) => {
        console.log('Recording activity:', activity);
    };

    const fetchEcoData = async () => {
        const token = localStorage.getItem('accessToken');

        if (!token) {
            navigate('/auth');
            return;
        }

        try {
            const response = await axios.get('http://localhost:8000/api/eco-tracking/', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setEcoData(response.data);
        } catch (error) {
            console.error('Error fetching eco data:', error);
            recordActivity({
                error: error.response ? error.response.data : 'Unknown error',
                status: error.response ? error.response.status : 'No status',
                message: error.message,
                timestamp: new Date().toISOString(),
            });

            if (error.response) {
                switch (error.response.status) {
                    case 401:
                        setError('Session expired. Please log in again.');
                        navigate('/auth');
                        break;
                    case 403:
                        setError('Access denied. You do not have permission to view this data.');
                        navigate('/auth');
                        break;
                    default:
                        setError('Failed to fetch eco data. Please try again.');
                }
            } else {
                setError('Network error. Please check your connection.');
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchEcoData();
    }, [navigate]);

    const handleSubmitSuccess = (newData) => {
        // Update ecoData with the newly added data
        setEcoData((prevData) => [...prevData, newData]);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="dashboard-container">
            <h2>Eco Tracking Dashboard</h2>
            {error && (
                <div className="error-message">
                    {error}
                    <button onClick={() => fetchEcoData()}>Retry</button> {/* Retry button */}
                </div>
            )}
            <EcoChart ecoData={ecoData} />
            <DailyProgressChart data={dailyProgress} />
            <EcoTrackingForm onSubmitSuccess={handleSubmitSuccess} /> {/* Form for user input */}
        </div>
    );
};

export default Dashboard;
